import { useState } from "react"
import { json } from "react-router-dom"


const WorkoutForm = ()=>{

    const [title, setTitle] = useState('')
    const [load, setLoad] = useState('')
    [reps, setReps] = useState('')
    [error, setError] = useState(null)


    const handleSubmit = async(e)=>
    {
        e.preventDefault()//to prevent the default refreshing of the page

        const workout = {title,load,reps}

        const response = await fetch('/api/workouts',{
            method:"POST",
            body:json.stringify(workout),
            headers:{
                'Content-Type':'application/json'
            }
        })
        const json = await response.json()//converting the response to a json file

        if(!response.ok)
        {
            setError(json.error)
        }

        if(response.ok)
        {
            setTitle('')
            setLoad('')
            setReps('')
            setError(null)
            console.log("Succesfully added the new workout",json)
        }

    }

    return(
        <form className="create" onSubmit={handleSubmit}>
            <h3>Add a new workout</h3>

            <label>Excercise Title</label>
            <input
            type = 'text'
            onChange = {(e)=>{setTitle(e.target.value)}}
            value = {title}/>

            <label>Load(in Kg)</label>
            <input
            type = 'text'
            onChange = {(e)=>{setLoad(e.target.value)}}
            value = {load}/>

            <label>No of Reps</label>
            <input
            type = 'text'
            onChange = {(e)=>{setReps(e.target.value)}}
            value = {Reps}/>

            <button>Create workout</button>

        </form>
    )

}


export default WorkoutForm
